mapper.update:
update coupon
expire_date = #{expireDate}, bind_user_id = #{bindUserId}, status = #{status}, gmt_modify = now(6)
where coupon_id = #{couponId}

update coupon
use_time = #{useTime}, order_id = #{orderId}, status = #{status}, gmt_modify = now(6)
where coupon_id = #{couponId} and bind_user_id = #{bindUserId}

update coupon
status = #{status}, gmt_modify = #{gmtModify}
where expire_date < now(6)

mapper.sql:
from coupon

	1 = 1

		and use_time between #{beginTime} and #{endTime}


		and status = #{status}
